CREATE TABLE IF NOT EXISTS `references` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`DESCRIPTION`	TEXT,
	`IMAGE`	TEXT,
	`URL`	INTEGER,
	`CONTENT`	TEXT,
	`CACHE`	INTEGER DEFAULT 0,
	`DATE`	TEXT,
	`ACTIVE`	INTEGER DEFAULT 1
);
CREATE TABLE IF NOT EXISTS `news` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`DESCRIPTION`	TEXT,
	`IMAGE`	TEXT,
	`URL`	TEXT,
	`CONTENT`	TEXT,
	`CACHE`	INTEGER DEFAULT 0,
	`DATE`	TEXT,
	`ACTIVE`	INTEGER DEFAULT 1
);
CREATE TABLE IF NOT EXISTS `info` (
	`TAG`	TEXT UNIQUE,
	`VALUE`	TEXT
);
CREATE TABLE IF NOT EXISTS `categorises` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`DESCRIPTION`	TEXT,
	`FATHER`	INTEGER DEFAULT 0,
	`IMAGE`	TEXT,
	`URL`	TEXT,
	`CONTENT`	TEXT,
	`CACHE`	INTEGER DEFAULT 0,
	`DATE`	TEXT,
	`ACTIVE`	INTEGER DEFAULT 1
);
