BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS `sync` (
	`TAG`	TEXT,
	`VALUE`	TEXT
);
CREATE TABLE IF NOT EXISTS `references` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`DESCRIPTION`	TEXT,
	`IMAGE_LIST`	TEXT,
	`DATE`	TEXT,
	`ACTIVE`	INTEGER DEFAULT 1,
	`FAQS`	TEXT,
	`CONTENT`	TEXT,
	`counter`	INTEGER DEFAULT 0,
	`cache`	INTEGER DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `news` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`DESCRIPTION`	TEXT,
	`IMAGE`	TEXT,
	`CONTENT`	TEXT,
	`ACTIVE`	INTEGER DEFAULT 1,
	`DATE`	TEXT,
	`counter`	INTEGER DEFAULT 0,
	`cache`	INTEGER DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `company` (
	`TAG`	TEXT,
	`VALUE`	TEXT
);
CREATE TABLE IF NOT EXISTS `categorises` (
	`ID`	INTEGER PRIMARY KEY AUTOINCREMENT,
	`TITLE`	TEXT,
	`FATHER`	INTEGER DEFAULT 0,
	`ACTIVE`	INTEGER DEFAULT 1,
	`IMAGE`	TEXT,
	`PAGE`	TEXT,
	`FAQS`	TEXT,
	`CONTENT`	TEXT,
	`DESCRIPTION`	TEXT,
	`counter`	INTEGER DEFAULT 0,
	`cache`	INTEGER DEFAULT 0
);
COMMIT;
